<script setup type="ts">
let showSidebar = ref(false);
const sidebar = ref(null);
const sidebarToggle = ref(null);

 const handleSidebar = (e) => {
  if (sidebar.value && !sidebar.value.contains(e.target) && !sidebarToggle.value.contains(e.target)) {
    showSidebar.value = false;
  }
};
 onMounted(()=>{
 	document.addEventListener('click', handleSidebar);
 });
 onBeforeUnmount(()=>{
 	document.removeEventListener('click', handleSidebar);
 });
</script>

